function [fitresult, gof] = createFit_Single_exp(xaxis, OffHist_norm)
%CREATEFIT(XAXIS,OFFHIST_NORM)
%  Create a fit.
%
%  Data for 'single exp fit 1' fit:
%      X Input : xaxis
%      Y Output: OffHist_norm
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 22-Jul-2022 19:26:16


%% Fit: 'single exp fit 1'.
[xData, yData] = prepareCurveData( xaxis, OffHist_norm );

% Set up fittype and options.
ft = fittype( 'exp1' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.StartPoint = [0.133246212944363 -0.025012489300758];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Create a figure for the plots.
figure( 'Name', 'single exp fit 1' );

% Plot fit with data.
subplot( 2, 1, 1 );
h = plot( fitresult, xData, yData );
legend( h, 'OffHist_norm vs. xaxis', 'single exp fit 1', 'Location', 'NorthEast' );
set (gca, 'Yscale', 'log')
% Label axes
xlabel('\xi [\mum]','FontSize',14);
ylabel('P(\xi)','FontSize',14);
grid on

% Plot residuals.
subplot( 2, 1, 2 );
h = plot( fitresult, xData, yData, 'residuals' );
legend( h, 'single exp fit 1 - residuals', 'Zero Line', 'Location', 'NorthEast' );
% Label axes
xlabel('\xi [\mum]','FontSize',14);
ylabel('P(\xi)','FontSize',14);
grid on


